sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var a = new GlideRecord('x_80404_bugs_bunny_m2m_guests_reservations');
	a.addQuery('reservation',current.reservation);
	a.addQuery('lead',true);
	a.addQuery('sys_id','!=',current.sys_id);
	gs.addInfoMessage('current sysid in m2m table of the leading passenger is ' + current.sys_id);
	a.query();
	while(a.next()){
		gs.addInfoMessage(a.guest.name+' won\'t be a leading passenger anymore. sys_id of the record in m2m table is  ' + a.sys_id+ ' Please reload the form to see changes.');
		a.lead = false;
		a.update();
	}

})(current, previous);
